import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface DeleteMessageProps {
  channelId: number;
}

const DeleteMessage: React.FC<DeleteMessageProps> = ({ channelId }) => {
  const [messageId, setMessageId] = useState('');

  // メッセージの削除ハンドラ
  const handleDelete = async () => {
    try {
      // メッセージの削除リクエストを送信
      await axios.get(`https://curriculum-3-muta-yuichiro-hackathon-eaq52kewiq-uc.a.run.app/delete-message?message_id=${messageId}`);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <div>
        <input type="text" value={messageId} onChange={(e) => setMessageId(e.target.value)} placeholder="Enter message ID" />
        <button onClick={handleDelete}>Delete</button>
      </div>
    </div>
  );
};

export default DeleteMessage;


