import React, { useState, useEffect } from "react";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { auth } from "./firebase";
import { useNavigate, Navigate } from "react-router-dom";
import Chat from "./Chat";

type Channels = {
  channel_id: string;
  name: string;
  description: string;
};

const Mypage: React.FC = () => {
  const [user, setUser] = useState<any>("");
  const [loading, setLoading] = useState(true);
  const [channels, setChannels] = useState<Channels[]>([]);

  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
  }, []);

  const navigate = useNavigate();

  const logout = async () => {
    await signOut(auth);
    navigate("/login/");
  };

  const fetchUsers = async () => {
    try {
      const res = await fetch(`http://localhost:8000/mypage?email=${user && user.email}`);
      if (!res.ok) {
        throw Error(`Failed to fetch users: ${res.status}`);
      }
      const contentsData: Channels[] = await res.json();
      setChannels(contentsData);
    } catch (err) {
      console.error(err + "1");
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <>
      {!loading && (
        <>
          {!user ? (
            <Navigate to={`/login/`} />
          ) : (
            <>
              <h1>マイページ</h1>
              <p>{user && user.email}</p>
              <ul>
          <h6>channel_id, name, description</h6>
            {channels.map((channels: Channels) => (
              <h6>
              {channels.channel_id}, {channels.name}, {channels.description}
              </h6>
          ))}
        </ul>
              <button onClick={logout}>ログアウト</button>
            </>
          )}
        </>
      )}
    </>
  );
};

export default Mypage;
