import React, { useState, useEffect } from "react";
import { BrowserRouter, Route, Routes, Link, useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import {userEmail} from "./Mypage";

interface Channel {
  channel_id: number;
  channel_name: string;
  messages: Message[];
}

interface Message {
  message_id: {
    String: string;
    Valid: boolean;
  };
  channel_id: number;
  user_id: string;
  content: {
    String: string;
    Valid: boolean;
  };
  edited_at: {
    String: string;
    Valid: boolean;
  };
}

interface UserData {
  user_id: string;
  channels: Channel[];
}

interface ChannelSelectProps {
  userData: UserData | null;
}

const ChannelSelect: React.FC<ChannelSelectProps> = ({ userData }) => {
  if (!userData) {
    return null;
  }

  return (
    <div>
      <h2>Select a channel:</h2>
      <ul>
        {userData.channels.map((channel) => (
          <li key={channel.channel_id}>
            <Link to={`/channel/${channel.channel_name}`}>{channel.channel_name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

interface ChannelPageProps {
  channelName: string;
  channels: Channel[];
}

interface ChannelParams {
    channelName: string;
  }


const ChannelPage: React.FC<ChannelPageProps> = ({ channelName, channels }) => {
  const channel = channels.find((channel) => channel.channel_name === channelName);

const navigate = useNavigate();



  if (!channel) {
    return null;
  }

  const { messages } = channel;


  const getMessages = async () => {
    navigate("/GetMessages/");
  };

  return (
    <div>
      <h2>Channel: {channelName}</h2>
      <h3>Messages:</h3>
      <ul>
        {messages.map((message) => (
          <li key={message.message_id.String}>
            <p>User ID: {message.user_id}</p>
            <p>Content: {message.content.String}</p>
            <p>Edited At: {message.edited_at.String}</p>
          </li>
        ))}
      </ul>
      <button onClick={getMessages}>チャンネルにメッセージを送る</button>
    </div>
  );
};

const GetUserChannels2: React.FC = () => {
  const [userData, setUserData] = useState<UserData | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const { channelName } = useParams<{ channelName: string }>();
  const navigate = useNavigate();



  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8081/user-channels?email=${userEmail}`);
        setUserData(response.data);
        setIsLoading(false);
      } catch (error) {
        setError("Failed to fetch user data");
        setIsLoading(false);
      }
    };

    fetchUserData();
  }, []);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
  <h1>User Data:</h1>
  <BrowserRouter>
    <Routes>
        <Route
          path="/user-channels"
          element={<ChannelSelect userData={userData} />}
        />
        <Route
          path="/channel/:channelName"
          element={<ChannelPage channels={userData?.channels || []} channelName={channelName || ""} />}
        />
    </Routes>
  </BrowserRouter>
</div>
  );
};

export default GetUserChannels2;
